import { createContext, useEffect, useReducer } from "react";


export const UserContext = createContext();
export const UserReducer = (state, action) => {
    switch (action.type) {
        case "ADD_DOC":
            const { id, name, username } = action.payload;
            const newDoc = {id, name, username}
            
            return [...state, newDoc];
        case "DETAILS":
            //navigate
            console.log(action.payload)
        case "TOGGLE_STYLE":
            console.log(state)
            setTheme(false);
           
  default:
    return state;
  }
};

const getUsersFromStorage = () => {
    const localData = localStorage.getItem("users");
    return localData ? JSON.parse(localData) : [];
};

const setUserInStorage = (users) => localStorage.setItem("users", JSON.stringify(users));
const setTheme = (data) => localStorage.setItem("darkmode", data);

const UserContextProvider = ({ children }) => {
    //const [users, setUsers] = useState(getUsersFromStorage());
    const [users, dispatch] = useReducer(
        UserReducer, // Pasamos nuestro reducer
        {},
        getUsersFromStorage //inicializa estado
    );


    useEffect(() => {
        setUserInStorage(users);
        setTheme(false);
    }, [users]);


   // const addUser = (user) => {
   //     setUsers((prevUsers) => [...prevUsers, user]);
   // };
    const addUser = (user) => dispatch({ type: "ADD_DOC", payload: user });
    const clickToDetails = (user) => dispatch({type: "DETAILS", payload: user});
    const toggleStyle = () => {dispatch({ type: "TOGGLE_STYLE" });};
    return (
        <UserContext.Provider value={{users, addUser , clickToDetails, toggleStyle}}>
        {children}
        </UserContext.Provider>
    );
};
export default UserContextProvider;
